number_column.script:
function onInit()
	registerMenuItem(Interface.getString("menu_reparse_hitpoints"), "spell_heal", 3);
end
function onMenuSelection(selection)
	if selection == 3 then
		HpManagerFZ.updateNpcHitPoints(window.getDatabaseNode());
	end
end
